mapper.select:
select
id, district_name, city_id, create_time, creator, operate_time, operator, delete_flag
from film_ticket.t_district


        and id = #{id}


        and district_name = #{districtName}


        and city_id = #{cityId}


        and create_time = #{createTime}


        and creator = #{creator}


        and operate_time = #{operateTime}


        and operator = #{operator}


        and delete_flag = #{deleteFlag}
